mapper.select:
select

from goods_order
where is_delete = 0 and open_id = #{pageObject.openId}

  and order_status = 0


  and order_status = #{pageObject.id}


  and order_status = 6

select count(1) from goods_order where is_delete = 0 and open_id = #{pageObject.openId}
and order_status = 0
and order_status = #{pageObject.id}
and order_status = 6

SELECT DISTINCT goods_order.*,user_base.nick_name,user_base.icon FROM goods_order LEFT JOIN user_base ON goods_order.open_id = user_base.open_id LEFT JOIN `share` on user_base.open_id = `share`.open_id WHERE goods_order.is_delete = 0 and `share`.is_delete = 0 and `share`.parent_open_id = #{openId}
and date_format(goods_order.create_time,'%Y-%m') = #{page.date}
and goods_order.order_status = #{page.type}
order by goods_order.create_time desc limit #{page.startIndex},#{page.pageSize}

select  count(1)
from (
  SELECT
     DISTINCT goods_order.uuid
  FROM
  goods_order
  LEFT JOIN user_base ON goods_order.open_id = user_base.open_id
  LEFT JOIN `share` on user_base.open_id = `share`.open_id
  WHERE
  goods_order.is_delete = 0
  and `share`.is_delete = 0
  and `share`.parent_open_id = #{openId}

    and date_format(goods_order.create_time,'%Y-%m') = #{page.date}


    and goods_order.order_status = #{page.type}

) p

SELECT goods_order.*,goods_order_detail.uuid as detailUuid,user_base.nick_name,user_base.icon FROM goods_order LEFT JOIN goods_order_detail ON goods_order.uuid = goods_order_detail.order_uuid LEFT JOIN user_base ON goods_order.open_id = user_base.open_id LEFT JOIN `share` on user_base.open_id = `share`.open_id WHERE goods_order.is_delete = 0 and `share`.is_delete = 0 and `share`.parent_open_id = #{openId} and goods_order_detail.is_refund = 1
and date_format(goods_order.create_time,'%Y-%m') = #{page.date}
order by goods_order.create_time desc limit #{page.startIndex},#{page.pageSize}
